var colors = ['rgba(255,234,0,0.5)','rgba(125,128,141,0.5)'];
//['#ffea00','#7d808c','#000000','#ff7b00'];




function loop()
{   ctx.fillStyle = 'black';
    ctx.fillRect(0,0,canvas.width,canvas.height);
    ctx.fillStyle = 'white';
    ctx.font = '30px Arial'
    ctx.fillText(player.points,100,100);
    
    for(let eachRow = 0; eachRow < rows; eachRow++)
    {   for(let eachColun = 0; eachColun < coluns; eachColun++)
        {   let arrayIndex = eachRow * coluns + eachColun;
            if(map[arrayIndex] >= 0)
            {   ctx.fillStyle = colors[map[arrayIndex]];
                ctx.fillRect(blockSize.width*eachColun - camera.x, blockSize.height*eachRow - camera.y, blockSize.width, blockSize.height);
            }            
        }
    }
    
    player.draw();
    player.update();
    camera.update();
    enemies.iterate();


    if(keys[1]) player.fire();
    if(keys[87]) player.move(0,-5);
    if(keys[83]) player.move(0,5);
    if(keys[68]) player.move(5,0);
    if(keys[65]) player.move(-5,0);
}